import React, { useState, useEffect } from 'react'
import { fetchFavorites } from './httpRequests'

export default function Members({ members, showMemberModal, showReposModal, follow, unfollow }){
    const [favorites, setFavorites] = useState([]);
    const [isFavorite, setIsFavorite] = useState(false);

    useEffect(() => {
        fetchFavorites().then((response) => {
            setFavorites(response.members);
          });
    })
    return (members.map((member) => {
        return( 
        <div key={member.id} id="div-container">
              <p onClick={() => {
                showMemberModal(member.login);
                }}>{member.login}</p>
              <img src={member.avatar_url} onClick={() => {
                showMemberModal(member.login);
                }}/>
                <br></br>
                <button onClick={() => {
                    showReposModal(member.login);
                }}>
                    Repos
                </button>
                {favorites && favorites.filter((datamem) => {
                    if(datamem.id === member.id){
                        return (<button onClick={() => {unfollow(member.id)}}>
                            Unfollow
                        </button>);
                    }
                    else{
                        return(
                            <button onClick={() => {follow(member.login, member.id)}}>
                                Follow
                            </button>
                        );
                    }
                })}
        </div>);
    }));
}
